object DataStore {
    var totalExpense: Double = 0.0
    var income: Double = 0.0
}

class MainActivity : AppCompatActivity() {

    private lateinit var viewPager: ViewPager2
    private lateinit var tabLayout: TabLayout

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)

        viewPager = findViewById(R.id.viewPager)
        tabLayout = findViewById(R.id.tabLayout)
        viewPager.adapter = ExpenseIncomePagerAdapter(this)

        TabLayoutMediator(tabLayout, viewPager) { tab, position ->
            tab.text = when (position) {
                0 -> "Expense"
                1 -> "Income"
                else -> ""
            }
        }.attach()
    }
}

class ExpenseIncomePagerAdapter(activity: FragmentActivity) : FragmentStateAdapter(activity) {
    override fun getItemCount(): Int = 2

    override fun createFragment(position: Int): Fragment =
        when (position) {
            0 -> ExpenseFragment()
            1 -> IncomeFragment()
            else -> ExpenseFragment()
        }
}

class ExpenseFragment : Fragment() {

    private lateinit var expense1: EditText
    private lateinit var expense2: EditText
    private lateinit var expense3: EditText
    private lateinit var expense4: EditText
    private lateinit var btnCalculate: Button

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?
    ): View? {
        val view = inflater.inflate(R.layout.fragment_expense, container, false)
        expense1 = view.findViewById(R.id.expense1)
        expense2 = view.findViewById(R.id.expense2)
        expense3 = view.findViewById(R.id.expense3)
        expense4 = view.findViewById(R.id.expense4)
        btnCalculate = view.findViewById(R.id.btnCalculate)
        return view
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        btnCalculate.setOnClickListener {
            val exp1 = expense1.text.toString().toDoubleOrNull() ?: 0.0
            val exp2 = expense2.text.toString().toDoubleOrNull() ?: 0.0
            val exp3 = expense3.text.toString().toDoubleOrNull() ?: 0.0
            val exp4 = expense4.text.toString().toDoubleOrNull() ?: 0.0

            val totalExpense = exp1 + exp2 + exp3 + exp4
            Toast.makeText(requireContext(), "Total Expenses: $totalExpense", Toast.LENGTH_SHORT).show()

            // Save the total expense for later use
            DataStore.totalExpense = totalExpense
        }
    }
}

class IncomeFragment : Fragment() {

    private lateinit var etIncome: EditText
    private lateinit var btnSubmit: Button

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?
    ): View? {
        val view = inflater.inflate(R.layout.fragment_income, container, false)
        etIncome = view.findViewById(R.id.etIncome)
        btnSubmit = view.findViewById(R.id.btnSubmit)
        return view
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        btnSubmit.setOnClickListener {
            val income = etIncome.text.toString().toDoubleOrNull() ?: 0.0
            Toast.makeText(requireContext(), "Income: $income", Toast.LENGTH_SHORT).show()

            // Save the income
            DataStore.income = income

            // Navigate to the summary activity using Intent
            val intent = Intent(requireContext(), SummaryActivity::class.java)
            startActivity(intent)
        }
    }
}

class SummaryActivity : AppCompatActivity() {

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_summary)

        // Assuming you have TextViews to show income and expenses
        val tvIncome: TextView = findViewById(R.id.tvIncome)
        val tvExpense: TextView = findViewById(R.id.tvExpense)

        tvIncome.text = "Income: ${DataStore.income}"
        tvExpense.text = "Expenses: ${DataStore.totalExpense}"

        val difference = DataStore.income - DataStore.totalExpense
        Toast.makeText(this, "Difference: $difference", Toast.LENGTH_LONG).show()
    }
}
