// Looping from 1 to 10 and printing each number
for (i in 1..10) {
    println("Number: $i")
}

// Looping through an array
val fruits = arrayOf("Apple", "Banana", "Cherry")
for (fruit in fruits) {
    println(fruit)
}

// A simple function that greets the user
fun greet(name: String): String {
    return "Hello, $name!"
}

// Usage
val greeting = greet("Alice")
println(greeting)  // Output: Hello, Alice!

// Using 'when' as a switch-case replacement in Kotlin
fun getDayName(day: Int): String {
    return when (day) {
        1 -> "Monday"
        2 -> "Tuesday"
        3 -> "Wednesday"
        4 -> "Thursday"
        5 -> "Friday"
        6 -> "Saturday"
        7 -> "Sunday"
        else -> "Invalid day"
    }
}

// Usage
println(getDayName(3))  // Output: Wednesday

// A simple if-else statement example
val number = 10
if (number % 2 == 0) {
    println("Even number")
} else {
    println("Odd number")
}
