package com.example.myapplication; // Package declaration

// Import necessary Android classes
import android.content.Intent;
import android.os.Bundle;
import android.view.View;
import android.widget.Button;
import android.widget.EditText;

import androidx.activity.EdgeToEdge;
import androidx.appcompat.app.AppCompatActivity;
import androidx.core.graphics.Insets;
import androidx.core.view.ViewCompat;
import androidx.core.view.WindowInsetsCompat;

import com.example.myapplication.R; // Import R file for resource access

public class MainActivity extends AppCompatActivity { // Define MainActivity class

    // Declare UI components
    private Button next; // Button to navigate to next activity
    private EditText name; // Input field to enter a name

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        EdgeToEdge.enable(this); // Enable edge-to-edge display
        setContentView(R.layout.activity_main); // Set layout file for this activity

        // Adjust UI elements to fit system bars (status bar, navigation bar, etc.)
        ViewCompat.setOnApplyWindowInsetsListener(findViewById(R.id.main), (v, insets) -> {
            Insets systemBars = insets.getInsets(WindowInsetsCompat.Type.systemBars());
            v.setPadding(systemBars.left, systemBars.top, systemBars.right, systemBars.bottom);
            return insets;
        });

        // Initialize UI components
        next = findViewById(R.id.button); // Find the button in the layout
        name = findViewById(R.id.editTextText); // Find the EditText input field in the layout

        // Set a click listener for the "next" button
        next.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                // Get the text entered by the user
                String n = name.getText().toString();

                // Create an intent to start MainActivity2 and pass the entered name
                Intent intent = new Intent(MainActivity.this, MainActivity2.class);
                intent.putExtra("nameUser", n); // Attach the name as extra data

                // Start MainActivity2
                startActivity(intent);
            }
        });
    }
}

package com.example.myapplication; // Package declaration

// Import necessary Android classes
import android.content.Intent;
import android.os.Bundle;
import android.view.View;
import android.widget.Button;
import android.widget.EditText;
import android.widget.TextView;
import android.text.TextUtils; // Import this to use isDigitsOnly()

import androidx.activity.EdgeToEdge;
import androidx.appcompat.app.AppCompatActivity;
import androidx.core.graphics.Insets;
import androidx.core.view.ViewCompat;
import androidx.core.view.WindowInsetsCompat;

public class MainActivity2 extends AppCompatActivity { // Define MainActivity2 class

    // Declare UI components
    private Button back;
    private TextView nametext;
    private EditText num1;
    private EditText num2;
    private EditText op;
    private Button calc;
    private TextView ans;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        EdgeToEdge.enable(this); // Enables edge-to-edge display
        setContentView(R.layout.activity_main2); // Set layout file for this activity

        // Adjust UI elements to fit window insets
        ViewCompat.setOnApplyWindowInsetsListener(findViewById(R.id.main), (v, insets) -> {
            Insets systemBars = insets.getInsets(WindowInsetsCompat.Type.systemBars());
          v.setPadding(systemBars.left, systemBars.top, systemBars.right, systemBars.bottom);
            return insets;
        });

        // Initialize UI components
        num1 = findViewById(R.id.editTextText2); // First number input field
        num2 = findViewById(R.id.editTextText4); // Second number input field
        op = findViewById(R.id.editTextText3);   // Operator input field
        calc = findViewById(R.id.button3);       // Calculate button
        ans = findViewById(R.id.textView8);      // TextView to display the result

        // Set click listener for calculate button
        calc.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                // Get input values and convert to integers
                int number1 = Integer.parseInt(num1.getText().toString());
                int number2 = Integer.parseInt(num2.getText().toString());
                int answer;
                char operator = op.getText().charAt(0); // Get the first character of the operator input

                // Perform calculation based on the entered operator
                if (operator == '+')
                    answer = number1 + number2;
                else if (operator == '-')
                    answer = number1 - number2;
                else if (operator == '*')
                    answer = number1 * number2;
                else if (operator == '/')
                    if(number2==0)
                    {
                        answer = -999999;
                    }
                    else {
                        answer = number1 / number2;
                    }
                else
                    answer = -99999; // Invalid operator case

                // Display result or error message
                if (answer == -99999)
                    ans.setText("Invalid operator entered");
                else if(answer == -999999)
                    ans.setText("Error:Divide by Zero");
                else
                    ans.setText(String.valueOf(answer));
            }
        });

        // Get the username from the previous activity and display it
        nametext = findViewById(R.id.textView3);
        String username = getIntent().getStringExtra("nameUser");
        nametext.setText(username);

        // Initialize back button and set its click listener
        back = findViewById(R.id.button2);
        back.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                // Create an intent to go back to MainActivity
                Intent intent = new Intent(MainActivity2.this, MainActivity.class);
                startActivity(intent);
            }
        });
    }
}
